Primario: Story = {
  args: {
    children: 'Enviar',
    variant: 'default',
    size: 'default',
    loading: false,
    disabled: false,
    asChild: false,
    onClick: fn(),
  },
  play: async ({ args, canvasElement }) => {
    const canvas = within(canvasElement)
    const button = canvas.getByRole('button', { name: /Enviar/i })
    await userEvent.click(button)
    await expect(args.onClick).toHaveBeenCalled()
  },
}

