Composicao: Story = {
  name: 'Composição em Botão',
  args: {
    status: 'error',
    size: 8,
  },
  render: (args) => (
    <Button
      onClick={action('button-click')}
      iconAfter={
        <Stack>
          <Dot {...args} />
          <Text
            fontSize={10}
            color="$color"
            position="absolute"
            top="-12px"
            right="-10px"
            backgroundColor="$background"
            borderRadius="$round"
            paddingHorizontal={4}
          >
            3
          </Text>
        </Stack>
      }
    >
      Notificações
    </Button>
  ),
  play: async ({ canvasElement }) => {
    const canvas = within(canvasElement)
    const button = canvas.getByRole('button', { name: /Notificações/i })
    await userEvent.click(button)
  },
  parameters: {
    docs: {
      description: {
        story:
          'O `Dot` pode ser facilmente composto dentro de outros componentes, como um `Button`, para criar um indicador de notificação.',
      },
    },
  },
}

Acessibilidade: Story = {
